import { Component } from "react";

class Cls_state extends Component{

    constructor(props){
        super();
        this.state={
            count:"0"
        };
    }
    add(){
        this.state
    }
    render(){
        return(
            <div>
                <h1>Use State in Class Component</h1>
                <p> count: {this.state.count} </p>
                <button>Add +2</button>
            </div>
        )
    }
}

export default Cls_state;
