import React, { useState } from "react";

const Timer = () => {
  const [time, setTime] = useState(0);
  const [intervalId, setIntervalId] = useState(null);

  const startTimer = () => {
    if (!intervalId) {
      const id = setInterval(() => {
        setTime((prevTime) => prevTime + 1);
      }, 1000);
      setIntervalId(id);
    }
  };

  const stopTimer = () => {
    if (intervalId) {
      clearInterval(intervalId);
      setIntervalId(null);
    }
  };

  const resetTimer = () => {
    stopTimer(); // Ensures interval is cleared before resetting
    setTime(0);
  };

  return (
    <div className="flex flex-col items-center p-5 border rounded-lg shadow-lg w-64">
      <h1 className="text-2xl font-bold mb-3">Stopwatch</h1>
      <p className="text-xl">{time} sec</p>
      <div className="mt-4 flex gap-2">
        <button onClick={startTimer} className="bg-green-500 text-white px-3 py-1 rounded">Start</button>
        <button onClick={stopTimer} className="bg-red-500 text-white px-3 py-1 rounded">Stop</button>
        <button onClick={resetTimer} className="bg-blue-500 text-white px-3 py-1 rounded">Reset</button>
      </div>
    </div>
  );
};

export default Timer;
